import React, { Component } from 'react';

// JSX

import TextInputGroup from '../layouts/TextInputGroup';
import axios from 'axios';

class EditContact extends Component {
  constructor(props) {
    super(props);

    this.nameInput = React.createRef();
    this.emailInput = React.createRef();
    this.cityInput = React.createRef();
    this.phoneInput = React.createRef();
    this.websiteInput = React.createRef();
    this.idInput = React.createRef();
  }

  state = {
    name: ' ',
    email: ' ',
    city: '',
    phone: ' ',
    website: '',
    id: 0,
    errors: {}
  };

  updateInformation = event => {
    event.preventDefault(); // Prevents page from reload
    this.setState({
      [event.target.name]: event.target.value
    });
  };

  handleContact = (value, event) => {
    const { name, email, phone } = this.state;

    const { id } = this.props.match.params;

    if (name === '') {
      this.setState({
        errors: { name: 'Name is required' }
      });
      return;
    }
    if (email === '') {
      this.setState({
        errors: { email: 'Email is required' }
      });
      return;
    }
    if (phone === '') {
      this.setState({
        errors: { phone: 'Phone is required' }
      });
      return;
    }
    const Person = {
      name: name,
      email: email,
      phone: phone,
      id: id,
      errors: {}
    };

    const response = axios.put(
      `https://localhost:1024/userUpdate/${id}`,
      Person
    );
  };

  render() {
    return (
      <Consumer>
        {value => (
          <div className="Edit-Contact-Form">
            <TextInputGroup
              type="text"
              label="Name"
              className="name form-control"
              name="name"
              defaultValue={this.props.name}
              placeholder="Enter name"
              onChange={this.updateInformation}
              error={this.state.errors.name}
            />
            <TextInputGroup
              type="email"
              label="Email"
              className="email form-control"
              name="email"
              defaultValue={this.props.email}
              placeholder="Enter email"
              onChange={this.updateInformation}
              error={this.state.errors.email}
            />
            <TextInputGroup
              type="text"
              label="Phone"
              className="phone form-control"
              name="phone"
              defaultValue={this.props.phone}
              placeholder="Enter phone"
              onChange={this.updateInformation}
              error={this.state.errors.phone}
            />{' '}
            <button
              type="submit"
              className="btn btn-primary"
              onClick={() => this.handleContact(value)}
            >
              Save{' '}
            </button>
          </div>
        )}
      </Consumer>
    );
  }
}

export default EditContact;
