import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Axios from 'axios';

class App extends Component {
  state = {
    user: null
  };

  componentDidMount() {
    Axios.get('https://jsonplaceholder.typicode.com/users/1')
      .then(response => {
        this.setState({
          user: response.data
        });
      })
      .catch(error => {
        console.log('error = ' + error);
      });
  }
  render() {
    return (
      <div className="App">
        <h1>AJAX 1 (by REACT)</h1>
        {this.state.user ? (
          <div>
            Name: {this.state.user.name}, <br /> Email: {this.state.user.email}
          </div>
        ) : null}
      </div>
    );
  }
}

export default App;
