import React, { Component } from 'react';
import axios from 'axios';
import './App.css';

class App extends Component {
  state = {
    username: '',
    password: ''
  };

  // This function
  updateInput = event => {
    event.preventDefault(); // Prevents page from reload
    // if (!element.empty && element.complete)
    this.setState({
      [event.target.name]: event.target.value
    });
  };

  login = async () => {
    const result = await axios('http://localhost:1024/login', {
      method: 'post',
      data: {
        username: this.state.username,
        password: this.state.password
      },
      withCredentials: true
    });

    if (result.data.error === 0) {
      alert('login successful');
    }
  };

  logout = async () => {};

  getSecretData = async () => {
    const result = await axios('http://localhost:1024/content', {
      method: 'get',

      withCredentials: true
    });

    if (result.data.error === 0) {
      alert('Blocked! This is a secret area.');
    }
  };

  updateInputHandler = event => {
    this.setState({
      [event.target.name]: event.target.value
    });
  };
  render() {
    return (
      <div className="App">
        <h1>Authorization 1 - Sessions</h1>
        <div>
          Username:{' '}
          <input type="text" name="username" onChange={this.updateInput} />
          Password:{' '}
          <input type="password" name="password" onChange={this.updateInput} />
        </div>
        <button onClick={this.login}>Login</button>
        <button onClick={this.logout}>Logout</button>
        <button onClick={this.getSecretData}>Load Secrect Data</button>
      </div>
    );
  }
}

export default App;
