import React from 'react';

import { BrowserRouter as Button, Link } from 'react-router-dom';
/* This is a stateless component and it's imported by the "UserDetails"
  component. It show the data about a user by a table cell. Inside this
  cell there are a single column for each data.
*/
let User = props => {
  // Style for the Edit button
  let buttonStyle = {
    borderRadius: '8%',
    backgroundColor: 'black',
    color: 'silver',
    width: '36px',
    height: '36px',
    boxShadow:
      '0 4px 8px 0 rgba(128, 98, 31, 0.2), 0 6px 20px 0 rgba(128, 98, 31, 0.19)',
    fontSize: '1.0em',
    margin: '2%'
  };

  // Setting for the table cell
  let cellStyle = {
    textAlign: 'center',
    width: '80px',
    padding: '0 15px 0 15px'
  };

  // Creation of an email address
  let mailTo = 'mailto:' + JSON.stringify(props.email);

  // Creation of a website link
  let websiteLink = (
    <a target="_blank" href={'https://' + props.website}>
      {props.website}
    </a>
  );

  return (
    // All the information here shown is imported from the parent component (UserDetails)
    <tr style={cellStyle}>
      <td>{props.id}</td>
      <td>{props.name}</td>
      <td>
        <a href={mailTo}>{props.email}</a>
      </td>
      <td>{props.city}</td>
      <td>{props.phone}</td>
      <td>{websiteLink}</td>
      <td>
        <button style={buttonStyle}>
          {/* This button has a link inside that redirects to the EditUser component. */}
          <Link to={'/edituser/' + props.id} title="Edit user information">
            <i className="fas fa-user-edit" />
          </Link>
        </button>
      </td>
    </tr>
  );
};

export default User;
