import React, { Component } from 'react';

// JSX

import TextInputGroup from '../layouts/TextInputGroup';
import Axios from 'axios';

class AddContact extends Component {
  constructor(props) {
    super(props);

    this.nameInput = React.createRef();
    this.emailInput = React.createRef();
    this.phoneInput = React.createRef();
    this.idInput = React.createRef();
  }

  state = {
    name: ' ',
    email: ' ',
    city: '',
    phone: ' ',
    website: '',
    errors: {}
  };

  updateInformation = event => {
    event.preventDefault(); // Prevents page from reload
    // if (!element.empty && element.complete)
    this.setState({
      [event.target.name]: event.target.value
    });
  };

  handleNewContact = (value, event) => {
    // event.preventDefault(); // Prevents page from reload
    // let length = value.persons.length;
    const newId =
      value.persons.length !== 0
        ? value.persons[value.persons.length - 1].id + 1
        : 1;

    const { name, email, city, phone, website } = this.state;

    if (name === ' ') {
      this.setState({
        errors: { name: 'Name is required' }
      });
      return;
    }
    if (email === ' ') {
      this.setState({
        errors: { email: 'Email is required' }
      });
      return;
    }
    if (city === ' ') {
      this.setState({
        errors: { phone: 'City is required' }
      });
      return;
    }
    if (phone === ' ') {
      this.setState({
        errors: { phone: 'Phone is required' }
      });
      return;
    }
    if (website === ' ') {
      this.setState({
        errors: { phone: 'Please write a website' }
      });
      return;
    }
    const Person = {
      name: name,
      email: email,
      city: city,
      phone: phone,
      website: website,
      // id: newId,
      errors: {}
    };

    Axios.post(`http://localhost:1024/newuser`, Person).then(response => {
      alert(response);
    });

    // const Person = {
    //   name: this.nameInput.value,
    //   email: this.emailInput.value,
    //   phone: this.phoneInput.value
    //   // id:
    // };

    // event.currentTarget.reset(); // Eliminating all that was written by the user
    this.props.history.push('/'); // Keeping the previous page (in case the user
    // wants to go back)
  };

  render() {
    return (
      <div className="Add-Contact-Form">
        {/* <form onSubmit={this.handleNewContact}> */}
        <TextInputGroup
          type="text"
          label="Name"
          className="name form-control"
          name="name"
          placeholder="Enter name"
          onChange={this.updateInformation}
          error={this.state.errors.name}
        />
        <TextInputGroup
          type="email"
          label="Email"
          className="email form-control"
          name="email"
          placeholder="Enter email"
          onChange={this.updateInformation}
          error={this.state.errors.email}
        />
        <small id="emailHelp" className="form-text text-muted">
          We'll never share your email with anyone else.
        </small>
        <TextInputGroup
          type="text"
          label="City"
          className="city form-control"
          name="city"
          placeholder="Enter city name"
          onChange={this.updateInformation}
          error={this.state.errors.city}
        />
        <TextInputGroup
          type="text"
          label="Phone"
          className="phone form-control"
          name="phone"
          placeholder="Enter phone"
          onChange={this.updateInformation}
          error={this.state.errors.phone}
        />{' '}
        <small id="emailHelp" className="form-text text-muted">
          We'll never share your phone with anyone else.
        </small>
        <TextInputGroup
          type="text"
          label="Website"
          className="website form-control"
          name="website"
          placeholder="Enter website"
          // ref={this.phoneInput}
          // value="Ex: 200-299-2938947"
          onChange={this.updateInformation}
          error={this.state.errors.website}
        />{' '}
        <button
          type="submit"
          className="btn btn-primary"
          onClick={() => this.handleNewContact()}
        >
          Add Contact{' '}
        </button>
        {/* </form> */}
      </div>
    );
  }
}

export default AddContact;
