import React, { Component } from 'react';

// JSX

import TextInputGroup from '../layouts/TextInputGroup';
import Axios from 'axios';

class EditContact extends Component {
  constructor(props) {
    super(props);

    this.nameInput = React.createRef();
    this.emailInput = React.createRef();
    this.cityInput = React.createRef();
    this.phoneInput = React.createRef();
    this.websiteInput = React.createRef();
    this.idInput = React.createRef();
  }

  state = {
    name: ' ',
    email: ' ',
    city: '',
    phone: ' ',
    website: '',
    id: 0,
    errors: {}
  };

  // Getting the right user from the database once again
  async componentDidMount() {
    try {
      Axios.get(
        'http://localhost:1024/user?id=' + this.props.match.params.id
      ).then(response => {
        this.setState({
          name: response.data[0].name,
          email: response.data[0].email,
          city: response.data[0].city,
          phone: response.data[0].phone,
          website: response.data[0].website,
          id: response.data[0].id
        });
      });
    } catch (error) {
      console.log('error = ' + error);
    }
  }

  updateInformation = event => {
    event.preventDefault(); // Prevents page from reload
    this.setState({
      [event.target.name]: event.target.value
    });
  };

  handleContact = event => {
    const { id } = this.props.match.params;

    const { name, email, city, phone, website } = this.state;

    if (name === ' ') {
      this.setState({
        errors: { name: 'Name is required' }
      });
      return;
    }
    if (email === ' ') {
      this.setState({
        errors: { email: 'Email is required' }
      });
      return;
    }
    if (city === ' ') {
      this.setState({
        errors: { city: 'City is required' }
      });
      return;
    }
    if (phone === ' ') {
      this.setState({
        errors: { phone: 'Phone is required' }
      });
      return;
    }
    if (website === ' ') {
      this.setState({
        errors: { website: 'Please write a website' }
      });
      return;
    }
    const Person = {
      name: name,
      email: email,
      phone: phone,
      id: id,
      errors: {}
    };

    Axios.put(`http://localhost:1024/userUpdate`, Person).then(response => {
      alert('User successfully updated.');
    });
  };

  render() {
    return (
      <div className="Edit-Contact-Form">
        <TextInputGroup
          type="text"
          label="Name"
          className="name form-control"
          name="name"
          defaultValue={this.props.name}
          placeholder="Enter name"
          onChange={this.updateInformation}
          error={this.state.errors.name}
        />
        <TextInputGroup
          type="email"
          label="Email"
          className="email form-control"
          name="email"
          defaultValue={this.props.email}
          placeholder="Enter email"
          onChange={this.updateInformation}
          error={this.state.errors.email}
        />
        <TextInputGroup
          type="text"
          label="Phone"
          className="phone form-control"
          name="phone"
          defaultValue={this.props.phone}
          placeholder="Enter phone"
          onChange={this.updateInformation}
          error={this.state.errors.phone}
        />{' '}
        <button
          type="submit"
          className="btn btn-primary"
          onClick={() => this.handleContact()}
        >
          Save{' '}
        </button>
      </div>
    );
  }
}

export default EditContact;
