import React, { Component } from 'react';
import Axios from 'axios';

import User from '../layouts/User';

/* 
  This component gets the id of the chosen user. It is loaded via Routing.
  It sends a request to the server. Then it receives the response which is
  an array with the needed elements. These elements are store on the object
  named "user" from state. Finally the elements are shown on the table.
  This component call the stateless component "User". This last one has the
  representation of the unique table cell.
*/
export default class Users extends Component {
  // State with the only needed object, initialized with null value
  state = {
    user: null
  };

  async componentDidMount() {
    try {
      Axios.get(
        'http://localhost:1024/user?id=' + this.props.match.params.id
      ).then(response => {
        this.setState({
          user: response.data[0]
        });
      });
    } catch (error) {
      console.log('error = ' + error);
    }
  }

  render() {
    /*
    let panel = {
      // marginLeft: '20%',
      borderRadius: '3%',
      backgroundColor: '#AAA',
      boxShadow:
        '0 4px 8px 0 rgba(31, 31, 31, 0.2), 0 6px 20px 0 rgba(31, 31, 31, 0.19)',
      width: '100%'
    }; *

    let headersStyle = {
      textAlign: 'center',
      color: 'white'
      // backgroundColor: '#000'
    };*/

    return (
      <div className="Users">
        <table className="UsersDetails">
          <thead className="titleCells">
            <tr className="headersStyle">
              <th>ID</th>
              <th>Name</th>
              <th>Email</th>
              <th>City</th>
              <th>Phone</th>
              <th>Website</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody className="dataCells">
            {this.state.user ? (
              <User
                key={this.state.user.id}
                id={this.state.user.id}
                name={this.state.user.name}
                email={this.state.user.email}
                city={this.state.user.city}
                phone={this.state.user.phone}
                website={this.state.user.website}
              />
            ) : null}
          </tbody>
        </table>
      </div>
    );
  }
}
