import React from 'react';

import { BrowserRouter as Button, Link } from 'react-router-dom';

let User = props => {
  let buttonStyle = {
    borderRadius: '8%',
    backgroundColor: 'black',
    color: 'silver',
    width: '36px',
    height: '36px',
    boxShadow:
      '0 4px 8px 0 rgba(128, 98, 31, 0.2), 0 6px 20px 0 rgba(128, 98, 31, 0.19)',
    fontSize: '1.0em',
    margin: '2%'
  };

  let cellStyle = {
    textAlign: 'center',
    width: '80px',
    padding: '0 15px 0 15px'
  };

  let mailTo = 'mailto:' + JSON.stringify(props.email);

  let websiteLink = (
    <a target="_blank" href={'https://' + props.website}>
      {props.website}
    </a>
  );

  return (
    <tr style={cellStyle}>
      <td>{props.id}</td>
      <td>{props.name}</td>
      <td>
        <a href={mailTo}>{props.email}</a>
      </td>
      <td>{props.city}</td>
      <td>{props.phone}</td>
      <td>{websiteLink}</td>
      <td>
        <button style={buttonStyle}>
          <Link to={'/edituser/' + props.id} title="Edit user information">
            <i className="fas fa-user-edit" />
          </Link>
        </button>
      </td>
    </tr>
  );
};

export default User;
