import React, { Component } from 'react';
import Axios from 'axios';
import User from '../layouts/User';

export default class Users extends Component {
  state = {
    users: null
  };

  componentDidMount() {
    Axios.get('https://jsonplaceholder.typicode.com/users')
      .then(response => {
        this.setState({
          users: response.data
        });
      })
      .catch(error => {
        console.log('error = ' + error);
      });
  }

  // Remove user from the array using an id
  removeUserById = id => {
    const usersTemp = [...this.state.users];

    let finalUsers = usersTemp.filter(user => user.id !== id);

    this.setState({
      users: finalUsers
    });
  };

  render() {
    let panel = {
      borderRadius: '3%',
      backgroundColor: 'grey',
      boxShadow:
        '0 4px 8px 0 rgba(31, 31, 31, 0.2), 0 6px 20px 0 rgba(31, 31, 31, 0.19)',
      width: '50%'
    };
    return (
      <div className="App">
        <table className="UsersList" style={panel}>
          <thead>
            <tr className="titleCells">
              <th>ID</th>
              <th>Name</th>
              <th>Email</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody className="dataCells">
            {this.state.users
              ? this.state.users.map(user => {
                  return (
                    <User
                      key={user.id}
                      id={user.id}
                      name={user.name}
                      email={user.email}
                      removeUserHandler={this.removeUserById}
                    />
                  );
                })
              : null}
          </tbody>
        </table>
      </div>
    );
  }
}
