import React, { Component } from 'react';
import axios from 'axios';
import './App.css';

class App extends Component {
  state = {
    username: '',
    password: '',
    token: ''
  };

  // This function
  updateInput = event => {
    event.preventDefault(); // Prevents page from reload
    // if (!element.empty && element.complete)
    this.setState({
      [event.target.name]: event.target.value
    });
  };

  /* This function doesn't work. */
  loginByEnterKey = async event => {
    if (event.code === 13) {
      const result = await axios('http://localhost:1024/login', {
        method: 'post',
        data: {
          username: this.state.username,
          password: this.state.password
        },
        withCredentials: true
      });

      if (result.data.error === 0) {
        alert('Login successful');

        localStorage.setItem('token', result.data.token);
      }
    }
  };

  login = async () => {
    const result = await axios('http://localhost:1024/login', {
      method: 'post',
      data: {
        username: this.state.username,
        password: this.state.password
      },
      withCredentials: true
    });

    if (result.data.error === 0) {
      alert('Login successful');

      localStorage.setItem('token', result.data.token);
    }
  };

  logout = async () => {
    try {
      const token = localStorage.getItem('token');
      const result = await axios('http://localhost:1024/logout', {
        method: 'post',
        headers: {
          Authorization: 'Bearer ' + token
        }
      });
      if (result.data.error === 0) {
        alert(result.data.message);
      }
    } catch (e) {
      alert(e);
    }
  };

  getSecretData = async () => {
    try {
      const token = localStorage.getItem('token');
      const result = await axios('http://localhost:1024/content', {
        method: 'get',
        headers: {
          Authorization: 'Bearer ' + token
        }
      });

      if (result.data.error === 0) {
        alert('Blocked! ' + result.data.message);
      }
    } catch (e) {
      alert('Error: ' + e);
    }
  };

  updateInputHandler = event => {
    this.setState({
      [event.target.name]: event.target.value
    });
  };
  render() {
    return (
      <div className="App">
        <h1>Authorization 3 - JWT</h1>
        <div>
          Username:{' '}
          <input
            type="text"
            name="username"
            onChange={this.updateInput}
            onKeyPress={this.loginByEnterKey}
          />
          Password:{' '}
          <input
            type="password"
            name="password"
            onChange={this.updateInput}
            onKeyPress={this.loginByEnterKey}
          />
        </div>
        <button onClick={this.login}>Login</button>
        <button onClick={this.logout}>Logout</button>
        <button onClick={this.getSecretData}>Load Secrect Data</button>
      </div>
    );
  }
}

export default App;
