import React, { Component } from 'react';

// JSX

import TextInputGroup from '../layouts/TextInputGroup';
import Axios from 'axios';

export default class testComp extends Component {
  constructor() {
    super();
    this.state = { a: false, user: '' };
  }

  /*
  state = {
    user: ''
  };*/

  /*
componentDidUpdate(previousProps, previousState) {
  if (previousProps.data !== this.props.data) {
    // console.log(this.props.match.params.id);
    console.log('Testing...');
  }
}*/

  async componentDidMount() {
    try {
      Axios.get(
        'http://localhost:1024/user?id=' + this.props.match.params.id
      ).then(response => {
        this.setState({
          user: response.data[0]
        });
      });
    } catch (error) {
      console.log('error = ' + error);
    }
    console.log('Updated!');
  }

  render() {
    return (
      <div className="Edit-Contact-Form">
        <TextInputGroup
          type="text"
          label="Name"
          className="name form-control"
          name="name"
          defaultValue={this.state.user.name}
          placeholder="Enter name"
          onChange={this.updateInformation}
          error={this.state.errors.name}
        />
        <TextInputGroup
          type="email"
          label="Email"
          className="email form-control"
          name="email"
          defaultValue={this.state.user.email}
          placeholder="Enter email"
          onChange={this.updateInformation}
          error={this.state.errors.email}
        />
        <TextInputGroup
          type="text"
          label="City"
          className="city form-control"
          name="city"
          defaultValue={this.state.user.city}
          placeholder="Enter city name"
          onChange={this.updateInformation}
          error={this.state.errors.city}
        />
        <TextInputGroup
          type="text"
          label="Phone"
          className="phone form-control"
          name="phone"
          defaultValue={this.state.user.phone}
          placeholder="Enter phone"
          onChange={this.updateInformation}
          error={this.state.errors.phone}
        />
        <TextInputGroup
          type="text"
          label="Website"
          className="website form-control"
          name="website"
          defaultValue={this.state.user.website}
          placeholder="Enter website"
          onChange={this.updateInformation}
          error={this.state.errors.website}
        />{' '}
        <button
          type="submit"
          className="btn btn-primary"
          onClick={() => this.handleContact()}
        >
          Save{' '}
        </button>
      </div>
    );
  }
}
